// Check that dashboard overview is displayed
cy.get('[data-testid="dashboard-overview"]').should('be.visible');
// Check that recent activity section is displayed
cy.get('[data-testid="recent-activity-section"]').should('be.visible');
// Check that quick stats section is displayed
cy.get('[data-testid="quick-stats-section"]').should('be.visible');
// Check that upcoming reviews section is displayed
cy.get('[data-testid="upcoming-reviews-section"]').should('be.visible');
// Check that learning progress section is displayed
cy.get('[data-testid="learning-progress-section"]').should('be.visible');
// Check that recent resources section is displayed
cy.get('[data-testid="recent-resources-section"]').should('be.visible');

// Check that user greeting is displayed with username
cy.get('[data-testid="user-greeting"]').should('be.visible');
cy.get('[data-testid="user-greeting"]').should('contain', beforeEach_1.testUser.email.split('@')[0]);

// Check that quick navigation links are displayed
cy.get('[data-testid="quick-nav-resources"]').should('be.visible');
cy.get('[data-testid="quick-nav-knowledge"]').should('be.visible');
cy.get('[data-testid="quick-nav-learning-path"]').should('be.visible');
cy.get('[data-testid="quick-nav-analytics"]').should('be.visible');
// Test navigation to resources
cy.get('[data-testid="quick-nav-resources"]').click();
cy.url().should('include', '/resources');
cy.go('back');
// Test navigation to knowledge
cy.get('[data-testid="quick-nav-knowledge"]').click();
cy.url().should('include', '/knowledge');
cy.go('back');
// Test navigation to learning path
cy.get('[data-testid="quick-nav-learning-path"]').click();
cy.url().should('include', '/learning-path');
cy.go('back');
// Test navigation to analytics
cy.get('[data-testid="quick-nav-analytics"]').click();
cy.url().should('include', '/analytics');
cy.go('back');

// Check that recent activity items are displayed
cy.get('[data-testid="activity-item"]').should('have.length.at.least', 1);
// Check that activity items have timestamps
cy.get('[data-testid="activity-timestamp"]').should('have.length.at.least', 1);
// Check that activity items have descriptions
cy.get('[data-testid="activity-description"]').should('have.length.at.least', 1);

// Check that study time stat is displayed
cy.get('[data-testid="stat-study-time"]').should('be.visible');
// Check that resources completed stat is displayed
cy.get('[data-testid="stat-resources-completed"]').should('be.visible');
// Check that concepts reviewed stat is displayed
cy.get('[data-testid="stat-concepts-reviewed"]').should('be.visible');
// Check that goals completed stat is displayed
cy.get('[data-testid="stat-goals-completed"]').should('be.visible');

// Check that upcoming reviews section is displayed
cy.get('[data-testid="upcoming-reviews-section"]').should('be.visible');
// Check if there are any upcoming reviews
cy.get('body').then(function ($body) {
    if ($body.find('[data-testid="no-upcoming-reviews"]').length > 0) {
        // No upcoming reviews
        cy.get('[data-testid="no-upcoming-reviews"]').should('be.visible');
    }
    else {
        // Has upcoming reviews
        cy.get('[data-testid="review-item"]').should('have.length.at.least', 1);
        // Test starting a review session
        cy.get('[data-testid="start-review-session"]').click();
        cy.url().should('include', '/knowledge/review');
        cy.go('back');
    }
});

// Check that learning progress section is displayed
cy.get('[data-testid="learning-progress-section"]').should('be.visible');
// Check that progress chart is displayed
cy.get('[data-testid="learning-progress-chart"]').should('be.visible');
// Check that progress percentage is displayed
cy.get('[data-testid="learning-progress-percentage"]').should('be.visible');

// Check that recent resources section is displayed
cy.get('[data-testid="recent-resources-section"]').should('be.visible');
// Check if there are any recent resources
cy.get('body').then(function ($body) {
    if ($body.find('[data-testid="no-recent-resources"]').length > 0) {
        // No recent resources
        cy.get('[data-testid="no-recent-resources"]').should('be.visible');
    }
    else {
        // Has recent resources
        cy.get('[data-testid="resource-item"]').should('have.length.at.least', 1);
        // Test viewing a resource
        cy.get('[data-testid="resource-item"]').first().click();
        cy.url().should('include', '/resources/');
        cy.go('back');
    }
});

// Click on add quick metric button
cy.get('[data-testid="add-quick-metric"]').click();
// Fill out the quick metric form
cy.get('input[name="study_hours"]').type('1.5');
cy.get('input[name="topics"]').type('cypress,testing');
cy.get('input[name="focus_score"]').type('9');
// Submit the form
cy.get('button[type="submit"]').click();
// Verify the metric was added
cy.get('[data-testid="success-notification"]').should('be.visible');
// Verify the stats are updated
cy.get('[data-testid="stat-study-time"]').should('be.visible');

// Click on add resource button
cy.get('[data-testid="quick-add-resource"]').click();
// Verify we're on the add resource page
cy.url().should('include', '/resources/add');
// Go back to dashboard
cy.go('back');

// Click on start review button
cy.get('[data-testid="quick-start-review"]').click();
// Verify we're on the review session page
cy.url().should('include', '/knowledge/review');
// Go back to dashboard
cy.go('back');
