// Visit the registration page
cy.visit('/auth/register');
cy.url().should('include', '/auth/register');
// Fill out the registration form
cy.get('input[name="username"]').type(testUser.username);
cy.get('input[name="email"]').type(testUser.email);
cy.get('input[name="password"]').type(testUser.password);
cy.get('input[name="fullName"]').type(testUser.fullName);
// Submit the form
cy.get('button[type="submit"]').click();
// Should be redirected to login page after successful registration
cy.url().should('include', '/auth/login');
// Login with the newly created account
cy.get('input[name="username"]').type(testUser.username);
cy.get('input[name="password"]').type(testUser.password);
cy.get('button[type="submit"]').click();
// Should be redirected to dashboard after successful login
cy.url().should('include', '/dashboard');
// Verify user is logged in by checking for user-specific elements
cy.get('[data-testid="user-greeting"]').should('contain', testUser.username);
// Test navigation to protected routes
cy.get('[data-testid="nav-resources"]').click();
cy.url().should('include', '/resources');
cy.get('[data-testid="nav-learning-path"]').click();
cy.url().should('include', '/learning-path');
cy.get('[data-testid="nav-knowledge"]').click();
cy.url().should('include', '/knowledge');
cy.get('[data-testid="nav-analytics"]').click();
cy.url().should('include', '/analytics');
// Test logout functionality
cy.get('[data-testid="user-menu"]').click();
cy.get('[data-testid="logout-button"]').click();
// Should be redirected to login page after logout
cy.url().should('include', '/auth/login');
// Verify user cannot access protected routes after logout
cy.visit('/dashboard');
cy.url().should('include', '/auth/login');

