// Check if analytics page loaded properly
page_objects_1.analyticsPage.isAnalyticsPageLoaded().then(function (isLoaded) {
    if (!isLoaded) {
        cy.log('Analytics page not loaded properly, skipping test');
        page_objects_1.analyticsPage.takeScreenshot('analytics-not-loaded');
        return;
    }
    // Set custom date range
    var endDate = new Date().toISOString().split('T')[0];
    var startDate = new Date();
    startDate.setMonth(startDate.getMonth() - 3);
    var formattedStartDate = startDate.toISOString().split('T')[0];
    // Set date range using page object
    page_objects_1.analyticsPage.setDateRange(formattedStartDate, endDate);
    // Take screenshot after filter applied
    page_objects_1.analyticsPage.takeScreenshot('analytics-filtered');
    // Verify charts are updated after filtering
    page_objects_1.analyticsPage.verifyOverviewChartVisible().then(function (isVisible) {
        expect(isVisible).to.be.true;
    });
    // Reset filters
    page_objects_1.analyticsPage.resetFilters();
    page_objects_1.analyticsPage.takeScreenshot('analytics-filters-reset');
});

