// Verify token is set at minimum
cy.window().its('localStorage').invoke('getItem', 'token').should('not.be.null');
// Use POM to navigate to review section
page_objects_1.conceptsPage.elementExists(page_objects_1.conceptsPage['selectors'].navKnowledgeReview).then(function (exists) {
    if (exists) {
        page_objects_1.conceptsPage.navigateToReview();
        // Check if review dashboard is visible
        page_objects_1.conceptsPage.isReviewDashboardVisible().then(function (dashboardVisible) {
            if (!dashboardVisible) {
                cy.log('Review dashboard not found - backend may not be working correctly');
            }
        });
    }
    else {
        cy.log('Knowledge review navigation not found - backend may not be working correctly');
    }
});

// Navigate to the review section using POM
page_objects_1.conceptsPage.navigateToReview();
// Start a review session
page_objects_1.conceptsPage.startReviewSession();
// Check that the review session is displayed
page_objects_1.conceptsPage.isReviewSessionVisible().should('be.true');
// Rate the recall difficulty
page_objects_1.conceptsPage.rateConceptRecall(3); // Medium difficulty
// Submit the review
page_objects_1.conceptsPage.submitReview();
// Check if the session is complete or there are more concepts
page_objects_1.conceptsPage.isReviewComplete().then(function (isComplete) {
    if (isComplete) {
        // Review session is complete
        page_objects_1.conceptsPage.returnToDashboard();
        cy.url().should('include', '/knowledge');
    }
    else {
        // Next concept is displayed
        page_objects_1.conceptsPage.elementExists(page_objects_1.conceptsPage['selectors'].conceptContent).should('be.true');
    }
});

// Navigate to the review section
page_objects_1.conceptsPage.navigateToReview();
// Start a review session
page_objects_1.conceptsPage.startReviewSession();
// Get the concept ID from the content element
page_objects_1.conceptsPage.getCurrentConceptId().then(function (conceptId) {
    if (!conceptId) {
        cy.log('Concept ID not found - cannot complete test');
        return;
    }
    // Rate the recall as easy
    page_objects_1.conceptsPage.rateConceptRecall(5); // Easy recall
    // Submit the review
    page_objects_1.conceptsPage.submitReview();
    // Navigate to the concept details page
    cy.visit("/knowledge/concepts/".concat(conceptId));
    // Check that the next review date is updated
    page_objects_1.conceptsPage.elementExists(page_objects_1.conceptsPage['selectors'].nextReviewDate).should('be.true');
    // Check that the review history is updated
    page_objects_1.conceptsPage.elementExists(page_objects_1.conceptsPage['selectors'].reviewHistory).then(function (exists) {
        if (exists) {
            cy.get(page_objects_1.conceptsPage['selectors'].reviewHistory).should('contain', 'Easy');
        }
    });
});

// Navigate to the statistics section
page_objects_1.conceptsPage.navigateToStatistics();
// Check that the statistics dashboard is displayed
page_objects_1.conceptsPage.isStatisticsDashboardVisible().should('be.true');
// Check that the charts are displayed
page_objects_1.conceptsPage.isReviewHistoryChartVisible().should('be.true');
page_objects_1.conceptsPage.isRecallPerformanceChartVisible().should('be.true');
page_objects_1.conceptsPage.isConceptsByStatusChartVisible().should('be.true');

// Check that the concepts list is displayed
page_objects_1.conceptsPage.elementExists(page_objects_1.conceptsPage['selectors'].conceptsList).should('be.true');
// Filter by review status
page_objects_1.conceptsPage.filterByReviewStatus('due');
// Check that the URL includes the filter parameter
cy.url().should('include', 'status=due');
// Check that the filtered concepts list is displayed
page_objects_1.conceptsPage.elementExists(page_objects_1.conceptsPage['selectors'].conceptsList).should('be.true');
