// Check if analytics page loaded properly
page_objects_1.analyticsPage.isAnalyticsPageLoaded().then(function (isLoaded) {
    if (!isLoaded) {
        cy.log('Analytics dashboard not loaded properly, skipping test');
        page_objects_1.analyticsPage.takeScreenshot('analytics-dashboard-not-loaded');
        return;
    }
    // Set custom date range for last 7 days
    var endDate = new Date().toISOString().split('T')[0];
    var startDate = new Date();
    startDate.setDate(startDate.getDate() - 7);
    var formattedStartDate = startDate.toISOString().split('T')[0];
    // Apply date filter
    page_objects_1.analyticsPage.setDateRange(formattedStartDate, endDate);
    page_objects_1.analyticsPage.takeScreenshot('analytics-7-day-filter');
    // Verify URL contains filter parameters
    cy.url().then(function (url) {
        var hasDateParams = url.includes('start=') || url.includes('range=') || url.includes('period=');
        if (!hasDateParams) {
            cy.log('URL does not contain expected date parameters');
        }
    });
    // Verify charts are updated
    page_objects_1.analyticsPage.verifyOverviewChartVisible();
});

// Check if analytics page loaded properly
page_objects_1.analyticsPage.isAnalyticsPageLoaded().then(function (isLoaded) {
    if (!isLoaded) {
        cy.log('Analytics dashboard not loaded properly, skipping test');
        page_objects_1.analyticsPage.takeScreenshot('analytics-dashboard-not-loaded');
        return;
    }
    // Check if chart type selector exists
    cy.get('body').then(function ($body) {
        var hasChartTypeSelector = $body.find('[data-testid="chart-type-toggle"]').length > 0 ||
            $body.find('[data-testid="chart-type-selector"]').length > 0;
        if (hasChartTypeSelector) {
            // Try clicking on different chart types
            if ($body.find('[data-testid="chart-type-bar"]').length > 0) {
                cy.get('[data-testid="chart-type-bar"]').click();
                page_objects_1.analyticsPage.takeScreenshot('bar-chart-type');
            }
            if ($body.find('[data-testid="chart-type-line"]').length > 0) {
                cy.get('[data-testid="chart-type-line"]').click();
                page_objects_1.analyticsPage.takeScreenshot('line-chart-type');
            }
        }
        else {
            cy.log('Chart type selector not available');
            page_objects_1.analyticsPage.takeScreenshot('no-chart-type-selector');
        }
    });
});

