    if (!isLoaded) {
        cy.log('Resources page not loaded properly, skipping test');
        page_objects_1.resourcesPage.takeScreenshot('resources-not-loaded');
        return;
    }
    // Navigate to the new resource page
    page_objects_1.resourcesPage.visitNewResource();
    // Check if URL input is available
    page_objects_1.resourcesPage.isUrlInputAvailable().then(function (isAvailable) {
        if (!isAvailable) {
            cy.log('URL input not available, skipping test');
            return;
        }
        // Check if extract metadata button is available
        page_objects_1.resourcesPage.isExtractMetadataButtonAvailable().then(function (hasExtractButton) {
            if (!hasExtractButton) {
                cy.log('Extract metadata button not available, skipping test');
                return;
            }
            // Enter URL and extract metadata
            page_objects_1.resourcesPage.enterUrlAndExtractMetadata('https://example.com');
            // Check if metadata was populated
            page_objects_1.resourcesPage.isMetadataPopulated().then(function (isPopulated) {
                if (isPopulated) {
                    cy.log('Metadata successfully extracted and populated');
                    // Create unique title to identify the resource
                    var resourceTitle = "Metadata Resource ".concat(Date.now());
                    // Update the title to be unique
                    page_objects_1.resourcesPage.enterManualDetails(resourceTitle, 'Description from metadata with manual edits');
                    // Select resource type if not automatically selected
                    page_objects_1.resourcesPage.selectResourceType('article');
                    // Submit the form
                    page_objects_1.resourcesPage.submitResourceForm();
                    // Verify success notification
                    page_objects_1.resourcesPage.verifySuccessNotification().then(function (hasSuccess) {
                        if (hasSuccess) {
                            cy.log('Resource with metadata created successfully');
                        }
                        else {
                            cy.log('No success notification displayed after creating resource with metadata');
                        }
                    });
                }
                else {
                    cy.log('Metadata extraction may have failed or feature not fully implemented');
                    cy.log('Falling back to manual entry');
                    // Create unique title to identify the resource
                    var resourceTitle = "Metadata Failed Resource ".concat(Date.now());
                    // Enter manual details
                    page_objects_1.resourcesPage.enterManualDetails(resourceTitle, 'Manual entry after metadata extraction failed');
                    // Select resource type
                    page_objects_1.resourcesPage.selectResourceType('article');
                    // Submit the form
                    page_objects_1.resourcesPage.submitResourceForm();
                }
            });
        });
    });
});

