        page_objects_1.learningPathPage.takeScreenshot('learning-path-not-loaded');
        return;
    }
    // Check if goals exist before trying to delete
    cy.get('body').then(function ($body) {
        var hasGoals = $body.find('[data-testid="goal-item"]').length > 0;
