            cy.log('No goals found to delete, creating a test goal first');
            // Create a test goal first
            page_objects_1.learningPathPage.createGoal({
                title: "Test Goal to Delete ".concat(Date.now()),
                description: 'This goal will be deleted'
            });
        }
        // Get the title of the first goal before deleting
        var goalTitle;
        cy.get('[data-testid="goal-title"]').first().invoke('text').then(function (text) {
            goalTitle = text;
            // Delete the first goal
            page_objects_1.learningPathPage.deleteFirstGoal();
            // Verify goal was deleted successfully
            page_objects_1.learningPathPage.hasSuccessNotification().then(function (hasSuccess) {
                if (hasSuccess) {
                    cy.log('Goal deleted successfully');
                    // Verify the goal no longer appears in the list
                    if (goalTitle) {
                        page_objects_1.learningPathPage.goalExists(goalTitle).then(function (exists) {
                            cy.wrap(exists).should('be.false');
                        });
                    }
                }
                else {
                    cy.log('No success notification displayed after deleting goal');
                }
            });
        });
    });
});
